sys_variable_value.value:
/*global tf_terraform_record, tf_run, tf_workspace, tf_util, tf_http*/
/*
  Poll Destroy Workspace
    Finds any records marked where is-destroyable = true and
    sends the destroy API requests to Terraform

    The "Delete Workspace" example workflow catalog item will set the is_destroyable
    value, then create a terraform destroy plan.
    This workflow will then check the state of the planning workflow.
    If it's done planning, it will apply the destroy run.
    If it's done applying the destroy run, it will delete the workspace from Terraform.
*/

gs.info("Polling Workflow: terraform delete workspace started...");

// System Property variables for Terraform API
var terraform_record = new tf_terraform_record();
var run              = new tf_run();
var workspace        = new tf_workspace();
var util             = new tf_util();

// Search Terraform Table for any records with is_destroyable=true
var gr = new GlideRecord("x_325709_terraform_terraform");
gr.addQuery("is_destroyable", "=", true);
gr.addQuery("last_known_state", "!=", "deleted");
gr.query();

// Loop through all records
while (gr.next()) {
  var comment      = "";
  var tfWorkspace  = terraform_record.get(gr.workspace_name);
  var workspaceID  = String(tfWorkspace.workspace_id);
  var tfCurrentRun = workspace.getWorkspaceCurrentRun(gr.workspace_name);
  var tfRunState   = run.getRunState(tfCurrentRun.id).toLowerCase();

  // Move on to next record if the current run is not a destroy action
  if (run.isDestroyRun(tfCurrentRun.id) === "false" ) {
    continue; // move on to next record
  }

  switch (tfRunState) {
    case "planning":
      // no-op: move on until it's done
      break;

    case "planned":
      /* Apply Run */
      // If the destroy run is finished planning, we can confirm and apply the destroy run.
      var applyContent = run.createRunRequestContent(workspaceID, "Destroy triggered by scheduler");
      var requestJSON  = JSON.stringify(applyContent);

      var http = new tf_http("TF Runs", "Apply Run", requestJSON);
      http.setParam("run_id", String(tfCurrentRun.id));
      http.execute();

      gr.last_known_state = "destroy_applying";
      gr.update();
      comment = util.formatCommentBlockQuote("Terraform: destroy run applying");
      break;

    case "applied": case "planned_and_finished":
      /* Delete Workspace */
      // The destroy run removing all resources managed by terraform is complete so we can delete the workspace.
      // HTTP REST request to Terraform Workspace API
      var httpWorkspace = new tf_http("TF Workspaces", "Delete Workspace", null);
      httpWorkspace.setParam("workspace_name", String(gr.workspace_name));
      httpWorkspace.execute();

      gr.last_known_state = "deleted";
      gr.update();
      comment = util.formatCommentBlockQuote("Terraform: Workspace Deleted");
      break;

    case "applying":
      // no-op: move on until it's done
      break;

    case "discarded": case "errored": case "canceled":
      gr.last_known_state = "errored";
      gr.is_destroyable   = false;
      gr.update();
      comment = util.formatCommentBlockQuote("Terraform: destroy error, resetting to not destroyable");
      break;
  }

  terraform_record.postRITMComment(gr.requested_item, comment);
} // end while loop

gs.info("Polling Workflow: terraform delete workspace finished.");

